import { React, useState, useEffect } from 'react'
import styled from 'styled-components';
import { useNavigate } from "react-router-dom";

const Todo = () => {

  const [data, setData] = useState([])
  const [content, setContent] = useState('')
  const [edit, setEdit] = useState(null)
  const [editData, setEditData] = useState({
    id: null,
    todo: "",
    isCompleted: false,
    userId: 2
  })
  const [checked, setChecked] = useState(false)

  useEffect(() => {
    fetch("https://pre-onboarding-selection-task.shop/todos",{
      method : "GET",
      headers : {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${localStorage.access_token}`
      }
    })
    .then(res => res.json())
    .then(res => setData(res))
    .catch(err => console.log(err))
  },[])

  const handleSubmitButton = () => {

    const postData = {
      "id": data.length+1,
      "todo": content,
      "isCompleted": false,
      "userId": 1
    }

    fetch("https://pre-onboarding-selection-task.shop/todos",{
      method : "POST",
      headers : {
        "Content-Type" : "application/json",
        "Authorization": `Bearer ${localStorage.access_token}`
      },
      body : JSON.stringify(postData)
    })
    .then(() => window.location.reload())
    .catch(err => console.log(err))
  }

  const handleUpdateButton = (id) => {

    fetch(`https://pre-onboarding-selection-task.shop/todos/:${id}`,{
    method : "PUT",
    headers : {
      "Content-Type" : "application/json",
      "Authorization": `Bearer ${localStorage.access_token}`
    },
    body : JSON.stringify({
      todo: editData.todo,
      isCompleted: editData.isCompleted
    }
    )})
    .then(res => console.log(res))
    .catch(err => console.log(err))
  }

  const handleCheckboxClick = (el) => {
    fetch(`https://pre-onboarding-selection-task.shop/todos/:${el.id}`,{
      method : "PUT",
      headers : {
        "Content-Type" : "application/json",
        "Authorization": `Bearer ${localStorage.access_token}`
      },
      body : JSON.stringify({
        id: el.id,
        todo: el.todo,
        isCompleted: el.isCompleted,
        userId: el.userId
      })
    })
    .then(res => console.log(res))
    .catch(err => console.log(err))
  }

  console.log(editData)


  return (
    <TodoStyle>
      {data.map(el => {
        return (
          <li key={el.id}>
              <input type="checkbox" defaultChecked={el.isCompleted ? true : false} onClick={() => {
                handleCheckboxClick(el)
              }}/>
              {el.id === edit ?
              <>
              <input data-testid="modify-input" onChange={e => setEditData({
                id: el.id,
                todo: e.target.value,
                isCompleted: el.isCompleted,
                userId: el.userId
              })} value={editData.todo}/>
              <button data-testid="submit-button" onClick={() => handleUpdateButton(el.id)}>제출</button>
              <button data-testid="cancel-button" onClick={() => setEdit(null)}>취소</button>
              </>
              :
              <>
              <span>{el.todo}</span>
              <button data-testid="modify-button" onClick={() => {
                setEdit(el.id)
                setEditData({
                  id: el.id,
                  todo: el.todo,
                  isCompleted: el.isCompleted,
                  userId: el.userId
                })
              }}>수정</button>
              <button data-testid="delete-button">삭제</button>
              </>
              }
          </li>
        )
      })}
      <br/>
      <input data-testid="new-todo-input" onChange={e => setContent(e.target.value)} />
      <br/>
      <button data-testid="new-todo-add-button" onClick={handleSubmitButton}>추가</button>
    </TodoStyle>
  )
}

export default Todo

const TodoStyle = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin-top: 10vh;
`
