import { React, useState, useEffect } from 'react'
import styled from 'styled-components';
import { useNavigate } from "react-router-dom";

const Todo = () => {

  const [data, setData] = useState([])
  const [content, setContent] = useState('')
  const [edit, setEdit] = useState({
    boolean : false,
    id : null
  })

  useEffect(() => {
    fetch("https://pre-onboarding-selection-task.shop/todos",{
      method : "GET",
      headers : {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${localStorage.access_token}`
      }
    })
    .then(res => res.json())
    .then(res => setData(res))
    .catch(err => console.log(err))
  },[])

  const handleSubmitButton = () => {

    const postData = {
      "id": data.length+1,
      "todo": content,
      "isCompleted": false,
      "userId": 1
    }

    fetch("https://pre-onboarding-selection-task.shop/todos",{
      method : "POST",
      headers : {
        "Content-Type" : "application/json",
        "Authorization": `Bearer ${localStorage.access_token}`
      },
      body : JSON.stringify(postData)
    })
    .then(() => window.location.reload())
    .catch(err => console.log(err))
  }

  const handleUpdateButton = (id) => {

    // const putData = {
    //   "id" : 
    // }

    fetch(`https://pre-onboarding-selection-task.shop/todos/:${id}`,{
    method : "PUT",
    headers : {
      "Content-Type" : "application/json",
      "Authorization": `Bearer ${localStorage.access_token}`
    },
    body : {

    }
    })

  }

  // console.log(data)

  return (
    <TodoStyle>
      {data.map(el => {
        return (
          <li key={el.id}>
            <label>
              <input type="checkbox" defaultChecked={el.isCompleted ? true : false} />
              <span>{el.todo}</span>
              <button data-testid="modify-button" onClick={() => setEdit({boolean : true , id : el.id})}>수정</button>
              <button data-testid="delete-button">삭제</button>
            </label>
          </li>
        )
      })}
      <br/>
      <input data-testid="new-todo-input" onChange={e => setContent(e.target.value)} />
      <br/>
      <button data-testid="new-todo-add-button" onClick={handleSubmitButton}>추가</button>
    </TodoStyle>
  )
}

export default Todo

const TodoStyle = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin-top: 10vh;
`
